import React, { Component } from 'react';
import { connect } from 'react-redux';
import { buildStats, enhanceStatsWithPlugs } from './../../utils/stats.js';

class Stats extends Component {
	constructor(props) {
		super(props)

		this.state = {
			displayInfo: {},
			baseStats: [],
			perks: [],
			stats: []
		}
	}

	componentDidMount() {
		const { manifest, weapon } = this.props;
		if ( weapon.hash ) {
			this.setState({
				displayInfo: manifest.DestinyInventoryItemDefinition[weapon.hash].displayProperties,
				baseStats: buildStats(manifest, weapon.hash),
				perks: weapon.perks,
				stats: enhanceStatsWithPlugs(
					buildStats(manifest, weapon.hash),
					weapon.hash,
					weapon.perks, 
					weapon.mod, 
					weapon.masterwork
				)
			});
		}
	}

	componentDidUpdate(prevProps) {
		const { manifest, weapon } = this.props;
		console.log(this.props.weapon)

		if ( weapon.hash !== prevProps.weapon.hash ) { 	
			this.setState({
				displayInfo: manifest.DestinyInventoryItemDefinition[weapon.hash].displayProperties,
				baseStats: buildStats(manifest, weapon.hash),
				perks: weapon.perks,
				stats: buildStats(manifest, weapon.hash)
			});
		} 

		console.log(
			weapon.perks.map(perk => {
return perk
			})
		)

		// if () {

			// enhanceStatsWithPlugs(
			// 	buildStats(manifest, weapon.hash),
			// 	weapon.hash,
			// 	weapon.perks, 
			// 	weapon.mod, 
			// 	weapon.masterwork
			// )
		// }
	}

	render(){
		const {
			stats
		} = this.state;
		const { manifest, weapon } = this.props;
		const weaponSubType = manifest.DestinyInventoryItemDefinition[weapon.hash].itemSubType;

		return (
			<div className="weapon__stats">

				<ul className="stat-list">
					{ 
						stats.map(stat => {
							if ( stat !== undefined ) { 
								const { 
									statHash,
									displayProperties,
									value
								} = stat;

								if ( weaponSubType === 18 && statHash === 3555269338) {
									return null;
								}

								return (
									<li key={statHash}>
										{ displayProperties.name }: { value } 
									</li>
								)
							}

							return true;
						})
					}
				</ul>
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, { 

})(Stats);
