import React, { Component } from 'react';
import { connect } from 'react-redux';
import Current from './Current';
import Vanity from './Vanity';
import Perks from './Perks';
import Mods from './Mods';
import Masterworks from './Masterworks';

class Weapon extends Component {

	tooltipHandler = (e) => {
		console.log(e)
		// console.log("mouse location:", e.clientX, e.clientY)
		// var eventDoc, doc, body;

        // event = event || window.event; // IE-ism

        // // If pageX/Y aren't available and clientX/Y are,
        // // calculate pageX/Y - logic taken from jQuery.
        // // (This is to support old IE)
        // if (event.pageX == null && event.clientX != null) {
        //     eventDoc = (event.target && event.target.ownerDocument) || document;
        //     doc = eventDoc.documentElement;
        //     body = eventDoc.body;

        //     event.pageX = event.clientX +
        //       (doc && doc.scrollLeft || body && body.scrollLeft || 0) -
        //       (doc && doc.clientLeft || body && body.clientLeft || 0);
        //     event.pageY = event.clientY +
        //       (doc && doc.scrollTop  || body && body.scrollTop  || 0) -
        //       (doc && doc.clientTop  || body && body.clientTop  || 0 );
		// }

		// console.log(event.pageX, event.pageY)
	}

	render(){
		const { weapon } = this.props;

		if ( weapon.hash !== null ) {
			return (
				<div className="weapon__wrapper">
					<Vanity />
					<Perks />
					<Mods tooltipHandler={() => this.tooltipHandler() } />
					<Masterworks tooltipHandler={() => this.tooltipHandler() } />
					<Current />
				</div>
			)
		} else {
			return (
				<div className="weapon__wrapper empty">
					No gun selected.
				</div>
			)
		}
	}
}

const mapStateToProps = state => ({
	weapon: state.weapon,
});

export default connect(mapStateToProps, {})(Weapon);
