import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getMasterworks } from './../../utils/masterworks.js';

class Masterworks extends Component {

	constructor(props) {
		super(props)

		const { manifest, weapon } = this.props;

		this.state = {
			masterworks: getMasterworks(manifest, weapon.hash)
		}
	}

	render(){
		const {
			props: {
				manifest,
				weapon
			},
			state: {
				masterworks
			}
		} = this;


		return (
			<div className="weapon__tab weapon__masterworks">
				<span className="uppercase tracked-wide underline">Masterwork</span>
				{ 
					Object.keys(masterworks).map(stat => {
						if (masterworks[stat].active) {
							return (
								<React.Fragment>
									<span>title</span>
									<ul>
										{
											masterworks[stat].benefits.map(mw => {
												console.log(mw.hash)
												return (
													<li key={mw.hash}><img src={`https://bungie.net${mw.displayProperties.icon}`} /></li>
												)
											})
										}
									</ul>
								</React.Fragment>
							)
						}
					})
				}
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, {

})(Masterworks);
