import React, { Component } from 'react';
import { connect } from 'react-redux';
import { storeTooltipCoords } from './../../actions/tooltipActions';
import Current from './Current';
import Vanity from './Vanity';
import Perks from './Perks';
import Mods from './Mods';
import Masterworks from './Masterworks';

class Weapon extends Component {
	constructor(props) {
		super(props)

		this.state = {
			offsetX: 20,
			offsetY: 0
		}
	}

	tooltipHandler = () => {
		let x = window.event.x;
		let y = window.event.y;

		if ((x + 380) >= window.innerWidth) {
			x = window.innerWidth - 360;
		}
		// if ((x + 380) > window.innerWidth) {
		// 	 = window.innerWidth - 360;
		// }

		this.setState({
			offsetX: x,
			offsetY: y
		})
	}

	render(){
		const {
			state: {
				offsetX,
				offsetY
			},
			props: {
				weapon 
			}
		} = this;

		if ( weapon.hash !== null ) {
			return (
				<div className="weapon__wrapper">
					<Vanity />
					<Perks />
					<Mods 
						tooltipHandler={() => this.tooltipHandler()}
						offsetX={ offsetX }
						offsetY={ offsetY }
					/>
					<Masterworks 
						tooltipHandler={() => this.tooltipHandler()}
						offsetX={ offsetX }
						offsetY={ offsetY }
					/>
					<Current />
				</div>
			)
		} else {
			return (
				<div className="weapon__wrapper empty">
					No gun selected.
				</div>
			)
		}
	}
}

const mapStateToProps = state => ({
	weapon: state.weapon,
});

export default connect(mapStateToProps, {})(Weapon);
