import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
// import { storeWeapon, storePerks } from './../../actions/weaponActions';
// import SearchFilter from './SearchFilter';

// import yearTwo from './../../utils/weaponSets/yearTwo.js';
// const weaponList = [];
// yearTwo.forEach(set => {
// 	(set.weapons).map((weapon) => {
// 		return weaponList.push(weapon);
// 	})
// });

class Autocomplete extends Component {
	constructor(props) {
		super(props)

		this.state = {
			weaponList: [],
			filteredSuggestions: [],
			showSuggestions: false,
			userInput: '',
			manifestLoaded: false,
		}
	}

	componentDidUpdate() {
		const { manifest } = this.props;

		if ( Object.keys((manifest)).length !== 0 ) {
			let weaponList = [];

			// All Weapons
			Object.keys(manifest.DestinyInventoryItemDefinition).map(weapon => {
				return manifest.DestinyInventoryItemDefinition[weapon].itemType === 3
					? weaponList.push(manifest.DestinyInventoryItemDefinition[weapon])
					: null
			})

			// const filters = nextProps.filters;

			// Weapon Rarity
			// if ( filters.rarity ) {
			// 	weaponList = weaponList.filter(weapon => {
			// 		return weapon.inventory.tierTypeHash === filters.rarity 
			// 	})
			// }

			// // Weapon Type
			// if ( filters.weaponType ) {
			// 	weaponList = weaponList.filter(weapon => {
			// 		return weapon.itemSubType === filters.weaponType 
			// 	})
			// }

			// // Damage Type
			// if ( filters.damage ) {
			// 	weaponList = weaponList.filter(weapon => {
			// 		return weapon.defaultDamageTypeHash === filters.damage 
			// 	})
			// }

			this.setState({ 
				manifestLoaded: true,
				weaponList: weaponList,
				filteredSuggestions: weaponList
			})
		}
	}

	onChange = e => {
		const suggestions = this.state.weaponList;
		const userInput = e.currentTarget.value;

		const filteredSuggestions = suggestions.filter(
			suggestion => (suggestion.displayProperties.name).toLowerCase().indexOf(userInput.toLowerCase()) > -1
		);

		this.setState({
			activeSuggestion: 0,
			filteredSuggestions,
			showSuggestions: true,
			userInput: e.currentTarget.value
		})
	}

	render() {
		const {
			onChange,
			state: {
				userInput,
				manifestLoaded,
			}
		} = this;

		return (
			<React.Fragment>
				<div className="search__input">
					<input
						type='text'
						onChange={ onChange }
						value={ userInput }
						placeholder='Find a weapon.'
						id='search'
						disabled={ manifestLoaded === false ? true : false }
						autoComplete="off"
					/>
				</div>
			</React.Fragment>
		)
	}
}

// Autocomplete.propTypes = {
// 	storeWeapon: PropTypes.func.isRequired,
// };

const mapStateToProps = state => ({
	manifest: state.manifest,
});

export default connect(mapStateToProps, { })(Autocomplete);
