// import store from './../utils/store.js';
import { STORE_WEAPON, STORE_PERKS, STORE_MOD } from './types';

// const state = store.getState();

export const storeWeapon = (hash) => dispatch => {
	dispatch({
		type: STORE_WEAPON,
		payload: hash
	})
};



let perkSet = {
	slotOne: null,
	slotTwo: null,
	slotThree: null,
	slotFour: null,
};
export const storePerks = (perk, slot) => dispatch => {
	// start at 1 because of the intrinsic perk.
	if (slot === 1) {
		if (perkSet.slotOne.hash !== perk.hash) {
			perkSet.slotOne = perk;
		} else {
			perkSet.slotOne = null;
		}
	}

	if (slot === 2) {
		if (perkSet.slotTwo.hash !== perk.hash) {
			perkSet.slotTwo = perk;
		} else {
			perkSet.slotTwo = null;
		}
	}

	if (slot === 3) {
		if (perkSet.slotThree.hash !== perk.hash) {
			perkSet.slotThree = perk;
		} else {
			perkSet.slotThree = null;
		}
	}

	if (slot === 4) {
		console.log(perk, slot, perkSet)
		if (perkSet.slotFour.hash !== perk.hash) {
			perkSet.slotFour = perk;
		} else {
			perkSet.slotFour = null;
		}
	}


	dispatch({
		type: STORE_PERKS,
		payload: perkSet
	})
};



let modSlot = { hash: 0 };
export const storeMod = (mod) => dispatch => {
	console.log(modSlot.hash, mod.hash)
	if (modSlot.hash === mod.hash) {
		modSlot = { hash: 0 };
		console.log('reset')
	} else {
		modSlot = mod;
		console.log('new mod')
	}

	dispatch({
		type: STORE_MOD,
		payload: mod
	})
}
