import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import ReactTooltip from 'react-tooltip';
import { connect } from 'react-redux';
import { buildSingleStat, buildStats } from '../utils/stats';

class Tooltip extends Component {
	constructor(props){
		super(props)

		this.state = {

		}
	}

	getTooltipNode = (hash) => {
		let icon = this[hash];
		console.log(icon)
		const domNode = ReactDOM.findDOMNode(icon);

		// console.log(domNode)
	}

	render(){
		const {
			props: {
				hash,
				title,
				subtitle,
				description,
				detail,
				stats,

				manifest,
				weapon
			},
			getTooltipNode
		} = this;

		return (
			<ReactTooltip 
				id={`getContent-${hash}`}
				offset={{top: 0, right: 20}}
				overridePosition={ ({ left, top }, currentEvent, currentTarget, node) => {
					const d = document.documentElement;

					this.getTooltipNode(hash)

					left = Math.min(d.clientWidth - node.clientWidth, left);
					top = Math.min(d.clientHeight - node.clientHeight, top);

					left = Math.max(0, left);
					top = Math.max(0, top);

					if (left + 380 > window.innerWidth) {
						left = window.innerWidth - 380
					}

					// console.log(node)

					return { top, left }
				} }
			>
				<div className="tooltip" ref={(c) => this['hash'] = c}>
					<header>
						<h3>{ title }</h3>
						<span>{ subtitle }</span>
					</header>
					<div className="tooltip-content">
						{ description ? <pre className="description">{ description }</pre> : null }
						{ detail ? <span>detail</span> : null }
						{ stats ? <TooltipStats stats={ stats } manifest={ manifest } weapon={ weapon }/> : null }
					</div>
				</div>
			</ReactTooltip>
		)
	}
}

const TooltipStats = (props) => {
	const {
		stats,

		manifest,
		weapon
	} = props;

	return (
		<div className='tooltip-stats'>
			<ul>
				{
					stats.map((stat, index) => {
						const perkVal = buildSingleStat(
							buildStats(
								manifest,
								weapon.hash
							),
							stat,
							weapon.hash,
							weapon.mod,
							weapon.masterwork
						)

						return (
							<li key={ index } className="stat">
								{ 
									<React.Fragment>
										<span className="stat__desc">{ manifest.DestinyStatDefinition[stat.statTypeHash].displayProperties.name }</span>
										<span className="stat__value">
											{
												perkVal >= 0 
													? perkVal === 0
														? `Â±${perkVal}` 
														: `+${perkVal}` 
													: perkVal
											}
										</span>
									</React.Fragment>
								}
							</li>
						)
					})
				}
			</ul>
		</div>
	)
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, {

})(Tooltip);
