					// 									storeMasterwork(mw); 
					// 									toggleActiveMW(mw);
					// 									enhanceStatsWithPlugs(
					// 										buildStats(manifest, weapon.hash),
					// 										weapon.hash,
					// 										weapon.perks, 
					// 										weapon.mod, 
					// 										weapon.masterwork
					// 									)
					// 								} }>
					// 									<div className="mw-icon"><img src={`https://bungie.net${mw.displayProperties.icon}`} alt=""/></div>
					// 								</li>
					// 							)
					// 						})
					// 					}
					// 				</ul>
					// 			</section>
					// 		)
					// 	} else {
					// 		return false;
					// 	}
					// })
				}
			</div>
		</React.Fragment>
	)
}

const Catalyst = (props) => {
	const {
		catalyst,
		activeMW,
		toggleActiveMW,
		storeMasterwork,
	} = props;

	return (
		<React.Fragment>
			<span className="uppercase tracked-wide underline">Catalyst</span>
			<div className="masterwork-sections">
				<ul className="masterwork-list">
					{
						catalyst.map(mw => {
							return (
								<li key={mw.hash}
									className={ 
										activeMW.hash === mw.hash ? 'node active' : 'node' 
									}
									onClick={ () => { 
										storeMasterwork(mw); 
										toggleActiveMW(mw);
									} }>
										<div className="mw-icon">
											<img src={`https://bungie.net${mw.displayProperties.icon}`} alt="" />
										</div>
								</li>
							)
						})
					}
				</ul>
			</div>
		</React.Fragment>
	)
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, {
	storeMasterwork
})(Masterworks);
