import React from 'react';
import { connect } from 'react-redux';

let curatedList = [
	3116356268, // Sapre Rations
	755130877, // Last Man Standing
	1115104187, // Sole Survivor
	3027844941, // Erentil FR4
	3169616514, // Bite of the Fox
	334171687, // Waking Vigil
	4190932264, // Beloved
	2429822977, // Austringer
];

const Suggestions = (props) => {

	const {
		manifest,
		manifestLoaded,
		weaponList,
		filteredSuggestions,
		showSuggestions,
		userInput,
		onSuggestionClick
	} = props;

	let curated;
	if ( weaponList.length && manifestLoaded){
		curated = curatedList.map(test => {
			return weaponList.filter(weapon => { return weapon.hash === test })[0];
		})
	}

	return (
		<div className="search__suggestions">
			{
				showSuggestions && userInput 
					? filteredSuggestions.length 
						? <SuggestionList 
							filteredSuggestions={ filteredSuggestions }
							manifest={ manifest }
							onSuggestionClick={ onSuggestionClick } />
						: <SuggestionListEmpty />
					: curated
						? <CuratedList 
							curatedList={ curated }
							manifest={ manifest }
							onSuggestionClick={ onSuggestionClick } /> 
						: null
			}
		</div>
	)
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	manifestLoaded: state.manifestLoaded
});
export default connect(mapStateToProps, { })(Suggestions);


const CuratedList = (props) => {
	const { manifest, curatedList, onSuggestionClick } = props;

	return (
		<ul className="suggestions-list">
			{
				curatedList.map((suggestion) => {
					return (
						<li 
							key={ suggestion.hash } 
							onClick={() => { onSuggestionClick(suggestion.hash) }}
						>
							<div className="suggestion-icon">
								<img src={`https://www.bungie.net${manifest.DestinyInventoryItemDefinition[suggestion.hash].displayProperties.icon}`} alt=""/>
							</div>
							<div className="suggestion-title tracked-thin">{ manifest.DestinyInventoryItemDefinition[suggestion.hash].displayProperties.name }</div>
						</li>
					)
				})
			}
		</ul>
	)
}

const SuggestionList = (props) => {
	const { manifest, filteredSuggestions, onSuggestionClick } = props;

	return (
		<ul className="suggestions-list">
				{
					filteredSuggestions.map((suggestion) => {
						return (
							<li 
								key={ suggestion.hash } 
								onClick={() => { onSuggestionClick(suggestion.hash) }}
							>
								<div className="suggestion-icon">
									<img src={`https://www.bungie.net${manifest.DestinyInventoryItemDefinition[suggestion.hash].displayProperties.icon}`} alt=""/>
								</div>
								<div className="suggestion-title tracked-thin">{ manifest.DestinyInventoryItemDefinition[suggestion.hash].displayProperties.name }</div>
							</li>
						)
					})
				}
			</ul>
	)
}

const SuggestionListEmpty = () => {
	return (
		<ul className="suggestion-list no-suggestion">
			<em>No weapon found. Use your light.</em>
		</ul>
	)
}
