import React, { Component } from 'react';
// import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import SearchInput from './SearchInput';
import Filters from './Filters';
import Suggestions from './Suggestions';
import { storeWeapon, storePerks } from './../../actions/weaponActions';
import { storeRarity, storeWeaponType, storeDamage } from './../../actions/filterActions';

class Search extends Component {
	constructor(props) {
		super(props)

		this.state = {
			weaponList: [],
			filteredSuggestions: [],
			showSuggestions: false,
			userInput: '',
			currentRarity: 4008398120,
			currentWeaponType: 0,
			currentDamage: 0,
		}
	}

	componentDidMount(prevProps) {
		const { manifest } = this.props;
		const { currentRarity } = this.state;

		let fullList = [];

		// All Weapons
		Object.keys(manifest.DestinyInventoryItemDefinition).map(weapon => {
			return manifest.DestinyInventoryItemDefinition[weapon].itemType === 3
				? fullList.push(manifest.DestinyInventoryItemDefinition[weapon])
				: null
		})

		let filteredList = fullList.filter(weapon => { 
			return weapon.inventory.tierTypeHash === currentRarity
		})

		console.log(filteredList)

		this.setState({ 
			filteredSuggestions: filteredList,
			weaponList: fullList
		})
	}

	componentDidUpdate() {
		console.log(this.state.filteredSuggestions)
	}

	// SearchInput.js
	onInputChange = e => {
		const { 
			weaponList,
			currentRarity,
			currentWeaponType,
			currentDamage
		} = this.state;

		const { rarity, weaponType, damage } = this.props.filters;

		const userInput = e.currentTarget.value;

		let filteredList = weaponList;

		if (rarity) filteredList = weaponList.filter(weapon => { console.log('rar'); return weapon.inventory.tierTypeHash === currentRarity })

		if (weaponType) filteredList = weaponList.filter(weapon => { console.log('typ'); return weapon.itemSubType === currentWeaponType })

		if (damage) filteredList = weaponList.filter(weapon => { console.log('dmg'); return weapon.defaultDamageTypeHash === currentDamage })

		console.log(this.props.filters.rarity, currentRarity)

		let suggestions = filteredList.filter(
			suggestion => (suggestion.displayProperties.name).toLowerCase().indexOf(userInput.toLowerCase()) > -1
		);

		this.setState({
			filteredSuggestions: suggestions,
			showSuggestions: true,
			userInput: e.currentTarget.value
		})
	}

	// Filters.js
	changeRarity = (e) => {
		const { filteredSuggestions } = this.state;
		let toStore = e.target.value === 0 ? null : parseInt(e.target.value);
		let filteredList = filteredSuggestions;
		filteredList = filteredList.filter(weapon => { return weapon.inventory.tierTypeHash === toStore })
		this.setState({ 
			currentRarity: toStore,
			filteredSuggestions: filteredList
		}); 
		this.props.storeRarity(toStore);
	}
	changeWeaponType = (e) => {
		const { filteredSuggestions } = this.state;
		let toStore = e.target.value === 0 ? null : parseInt(e.target.value);
		let filteredList = filteredSuggestions;
		filteredList = filteredList.filter(weapon => { return weapon.itemSubType === toStore })
		this.setState({ 
			currentWeaponType: toStore,
			filteredSuggestions: filteredList
		}); 
		this.props.storeWeaponType(toStore);
	}
	changeDamage = (e) => {
		const { filteredSuggestions } = this.state;
		let toStore = e.target.value === 0 ? null : parseInt(e.target.value);
		let filteredList = filteredSuggestions;
		filteredList = filteredList.filter(weapon => { return weapon.defaultDamageTypeHash === toStore })
		this.setState({ 
			currentDamage: toStore,
			filteredSuggestions: filteredList
		}); 
		this.props.storeDamage(toStore);
	}


	// onSuggestionClick = (hash) => {
	// 	this.props.storeWeapon(hash);	
	// 	this.props.storePerks(null, 0);
	// 	this.props.storePerks(null, 1);
	// 	this.props.storePerks(null, 2);
	// 	this.props.storePerks(null, 3);

	// 	this.setState({
	// 		activeSuggestion: 0,
	// 		filteredSuggestions: [],
	// 		showSuggestions: false,
	// 		userInput: ''
	// 	})

	// 	console.log()
	// }

	render() {
		const {
			onInputChange,
			changeRarity,
			changeWeaponType,
			changeDamage,
			state: {
				filteredSuggestions,
				showSuggestions,
				userInput,
				currentRarity,
				currentWeaponType,
				currentDamage,
			},
			props: {
				manifestLoaded
			}
		} = this;

		return (
			<div className="search__wrapper">
				<SearchInput 
					onInputChange={ onInputChange }
					userInput={ userInput }
					manifestLoaded={ manifestLoaded }
				/>
				<Filters 
					changeRarity={ changeRarity }
					changeWeaponType={ changeWeaponType }
					changeDamage={ changeDamage }
					currentRarity={ currentRarity }
					currentWeaponType={ currentWeaponType }
					currentDamage={ currentDamage }
				/>
				<Suggestions 
					filteredSuggestions={ filteredSuggestions }
					showSuggestions={ showSuggestions }
					userInput={ userInput }
				/>
			</div>
		)
	}
}

// Autocomplete.propTypes = {
// 	storeWeapon: PropTypes.func.isRequired,
// 	storePerks: PropTypes.func.isRequired
// };

const mapStateToProps = state => ({
	manifest: state.manifest,
	manifestLoaded: state.manifestLoaded,
	filters: state.filters,
});

export default connect(mapStateToProps, { 
	storeWeapon, storePerks,
	storeRarity, storeWeaponType, storeDamage,
})(Search);
