import { STORE_TOOLTIP_COORDS } from '../actions/types';

const initialState = {
	x: 0,
	y: 0,
}

export default function(state = initialState, action) {
	const data = action.payload;

	switch(action.type) {
		case STORE_TOOLTIP_COORDS:
			return {
				...state,
				x: data.x,
				y: data.y,
			};

		default: 
		return state;
	}
}
