import React, { Component } from 'react';
import { connect } from 'react-redux';
import { storePerks } from './../../actions/weaponActions';

class Weapon extends Component {
	constructor(props) {
		super(props)

		const {
			manifest,
			weapon
		} = this.props;

		this.state = {
			weapon: {}
		}
	}
	componentDidUpdate(prevProps) {
		const { manifest } = this.props;

		if (prevProps.weapon.hash !== this.props.weapon.hash && this.props.weapon.hash !== null) {
			this.setState({
				weapon: manifest.DestinyInventoryItemDefinition[this.props.weapon.hash]
			})
		}
	}

	buildStats = () => {
		const {
			manifest,
			weapon
		} = this.props;

		let currentWeapon;

		if ( weapon.hash ) {
			currentWeapon = manifest.DestinyInventoryItemDefinition[weapon.hash]
		}

		console.log(currentWeapon)
	}

	render(){
		const {

		} = this;

		return (
			<div className="weapon__wrapper">
				weapon
				{ this.buildStats() }
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, { 
	storePerks
})(Weapon);
