import React, { Component } from 'react';
import { connect } from 'react-redux';
import Current from './Current';
import Vanity from './Vanity';
import Perks from './Perks';
import Mods from './Mods';
import Masterworks from './Masterworks';

class Weapon extends Component {

	tooltipHandler = (event) => {
		console.log(window.event)
	}

	render(){
		const { weapon } = this.props;

		if ( weapon.hash !== null ) {
			return (
				<div className="weapon__wrapper">
					<Vanity />
					<Perks />
					<Mods tooltipHandler={() => this.tooltipHandler() } />
					<Masterworks tooltipHandler={() => this.tooltipHandler() } />
					<Current />
				</div>
			)
		} else {
			return (
				<div className="weapon__wrapper empty">
					No gun selected.
				</div>
			)
		}
	}
}

const mapStateToProps = state => ({
	weapon: state.weapon,
});

export default connect(mapStateToProps, {})(Weapon);
