let manifest;

function containsOnly(array1, array2){
	return array2.every(elem => array1.includes(elem))
}

export function buildPerks(
	theManifest,
	weapon
) {
	console.log('----- Building perks. -----');

	// Set up inital variables.
	manifest = theManifest;
	const itemDef = manifest.DestinyInventoryItemDefinition[weapon.hash];
	const socketEntries = itemDef.sockets.socketEntries;

	// Item that will be returned, maximum of 4 perk slots. Not a good 
	// idea since a 5-perk weapon could come later on.
	let perkList = [
		[],
		[],
		[],
		[],
		[]
	];
	let slotCheck = 0;

	// Map through to find ones that are genuine perks.
	socketEntries.map(socket => {

		// Check plug sources to filter out MWs, mods, shaders, etc.
		if (socket.plugSources === 2) {
			socket.reusablePlugItems.map(plug => {
				const plugItemsCategories = manifest.DestinyInventoryItemDefinition[plug.plugItemHash].itemCategoryHashes;

				// Filter out Kill Trackers.
				if (plugItemsCategories.includes(59) && plugItemsCategories.length > 1) {

					console.log(socket.reusablePlugItems.length, socket.reusablePlugItems)
					// console.log(manifest.DestinyInventoryItemDefinition[plug.plugItemHash]	)

					for(let i = 0; i < socket.reusablePlugItems.length; i++){
						perkList[slotCheck].push(socket.reusablePlugItems[i].plugItemHash)

						console.log(socket.reusablePlugItems[i].plugItemHash, plug.plugItemHash)
					}	
					slotCheck++;
				}
			})
		}
	});

	console.log(perkList)

	// console.log(perkList.map(perk => manifest.DestinyInventoryItemDefinition[perk[0]].displayProperties.name ))
}
