import React, { Component } from 'react';
import { connect } from 'react-redux';
import { buildStats } from './../../../utils/stats.js';

class Vanity extends Component {
	constructor(props) {
		super(props)

		this.state = {
			baseStats: [],
			displayInfo: {},
		}
	}

	componentDidMount() {
		const { manifest, weapon } = this.props;
		if ( weapon.hash ) {
			this.setState({
				displayInfo: manifest.DestinyInventoryItemDefinition[weapon.hash].displayProperties,
				baseStats: (buildStats(manifest, weapon.hash)).splice(undefined),
			});
		}
	}

	componentDidUpdate(prevProps) {
		const { manifest, weapon } = this.props;
		if ( weapon.hash !== prevProps.weapon.hash ) { 
			this.setState({
				displayInfo: manifest.DestinyInventoryItemDefinition[weapon.hash].displayProperties,
				baseStats: (buildStats(manifest, weapon.hash)).splice(undefined),
			});
		}
	}

	render(){
		const {
			baseStats
		} = this.state;

		console.log(this.state.baseStats)

		return (
			<div className="weapon__wrapper">
				<ul>
				{ 
					baseStats.map(stat => {
						console.log(stat)

						if ( stat !== undefined ) { 
							const { 
								statHash,
								displayProperties,
								value
							} = stat;

							return (
								<li key={statHash}>
									{ displayProperties.name }: { value }
								</li>
							)
						}
					})
				}
				</ul>
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, { 

})(Vanity);
