import React, { Component } from 'react';
import { connect } from 'react-redux';
import { storeTooltipCoords } from './../../actions/tooltipActions';
import Current from './Current';
import Vanity from './Vanity';
import Perks from './Perks';
import Mods from './Mods';
import Masterworks from './Masterworks';

class Weapon extends Component {
	constructor(props) {
		super(props)

		this.state = {
			offsetX: 20,
			offsetY: 0
		}
	}

	tooltipHandler = () => {
		const x = window.event.x;
		const y = window.event.y;

		const coords = { x, y };
		return coords;
	}

	render(){
		const {
			state: {
				offsetX,
				offsetY
			},
			props: {
				weapon 
			}
		} = this;

		if ( weapon.hash !== null ) {
			return (
				<div className="weapon__wrapper">
					<Vanity />
					<Perks />
					<Mods 
						tooltipHandler={() => this.tooltipHandler()}
						offsetX={ offsetX }
						offsetY={ offsetY }
					/>
					<Masterworks 
						tooltipHandler={() => this.tooltipHandler()}
						offsetX={ offsetX }
						offsetY={ offsetY }
					/>
					<Current />
				</div>
			)
		} else {
			return (
				<div className="weapon__wrapper empty">
					No gun selected.
				</div>
			)
		}
	}
}

const mapStateToProps = state => ({
	weapon: state.weapon,
});

export default connect(mapStateToProps, {})(Weapon);
