import React, { Component } from 'react';
import { connect } from 'react-redux';
// import { buildEnhancedStat } from './../../utils/stats.js';

class Current extends Component {

	render(){
		const {
			props: {
				manifest,
				weapon: {
					perks,
					mod,
					masterwork
				}
			}
		} = this;
		// console.log('current', weapon)
		return (
			<div className="weapon__tab weapon__current">
				<span className="uppercase tracked-wide underline">Current</span>
				<div className="weapon__curent-perks">
					{
						Object.keys(perks).map(perk => {
							if ( perks[perk].hash !== 0) {
								return manifest.DestinyInventoryItemDefinition[perks[perk].hash].displayProperties.name
							}

						})
					}
				</div>
				<div className="weapon__curent-mod">mod</div>
				<div className="weapon__curent-masterwork">mw</div>
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, {

})(Current);
