import React, { Component } from 'react';
import { connect } from 'react-redux';
import { buildSingleStat } from '../utils/stats';

const Tooltip = (props) => {
	const {
		title,
		subtitle,
		description,
		detail,
		stats,

		manifest,
		weapon
	} = props;

	return (
		<div 
			className="tooltip"
		>
			<header>
				<h3>{ title }</h3>
				<span>{ subtitle }</span>
			</header>
			<div className="tooltip-content">
				<pre className="description">
					{ description }
				</pre>
				{ detail ? <span>detail</span> : null }
				{ stats ? <TooltipStats stats={ stats } manifest={ manifest } weapon={ weapon }/> : null }
			</div>
		</div>
	)
}

const TooltipStats = (props) => {
	const {
		stats,

		manifest,
		weapon
	} = props;

	return (
		<div className='tooltip-stats'>
			{
				stats.map(stat => {
					console.log(stat)
					buildSingleStat(
						buildStats(
							manifest,
							weapon.hash
						),
						stat,
						weapon.hash,
						weapon.mod,
						weapon.masterowrk
					)
				})
			}
		</div>
	)
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, {

})(Tooltip);
