import React, { Component } from 'react';
// import PropTypes from 'prop-types';
import { connect } from 'react-redux';
// import SearchFilter from './SearchFilter';

// import yearTwo from './../../utils/weaponSets/yearTwo.js';
// const weaponList = [];
// yearTwo.forEach(set => {
// 	(set.weapons).map((weapon) => {
// 		return weaponList.push(weapon);
// 	})
// });

class Suggestions extends Component {

	constructor(props) {
		super(props)

		const filSug = this.props.filteredSuggestions;

		this.state = {
			filteredSuggestions: this.props.filteredSuggestions
		}
	}

	componentDidUpdate(prevProps) {
		if ( !this.arraysEqual(prevProps.filteredSuggestions, this.state.filteredSuggestions) ) {
			this.setState({
				filteredSuggestions: prevProps.filteredSuggestions
			})

			console.log(filteredSuggestions)
		}
	}

	arraysEqual = (a, b) => {
		if (a === b) return true;
		if (a == null || b == null) return false;
		if (a.length != b.length) return false;

		// If you don't care about the order of the elements inside
		// the array, you should sort both arrays here.
		// Please note that calling sort on an array will modify that array.
		// you might want to clone your array first.

		for (var i = 0; i < a.length; ++i) {
			if (a[i] !== b[i]) return false;
		}
		return true;
	}

	render() {
		const {
			state: {
				filteredSuggestions
			},
			props: { manifest,
				showSuggestions,
				userInput,
			}
		} = this;

		return (
			<div className="search__suggestions">
				{
					showSuggestions && userInput 
						? filteredSuggestions.length 
							? <SuggestionList 
								filteredSuggestions={ filteredSuggestions }
								manifest={ manifest }
								/> 
							: <SuggestionListEmpty />
						: null
				}
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest
});
export default connect(mapStateToProps, { })(Suggestions);


const SuggestionList = (props) => {
	const { filteredSuggestions, manifest } = props;

	return (
		<ul className="weapon-list__suggestions">
				{
					filteredSuggestions.map((suggestion) => {
						return (
							<li 
								key={ suggestion.hash } 
								onClick={() => { this.onClick(suggestion.hash) }}
							>
								<div className="weapon-list__item-icon">
									<img src={`https://www.bungie.net${manifest.DestinyInventoryItemDefinition[suggestion.hash].displayProperties.icon}`} alt=""/>
								</div>
								<div className="weapon-list__item-title">
									<span className='weapon-list__item-name'>{ manifest.DestinyInventoryItemDefinition[suggestion.hash].displayProperties.name }</span>
									<span className='weapon-list__item-type'>{ manifest.DestinyInventoryItemDefinition[suggestion.hash].itemTypeDisplayName }</span>
								</div>
							</li>
						)
					})
				}
			</ul>
	)
}

const SuggestionListEmpty = (props) => {
	return (
		<ul className="weapon-list__suggestions no-suggestion">
			<em>No weapon found. Use your light.</em>
		</ul>
	)
}
