import React, { Component } from 'react';
import { connect } from 'react-redux';
import { buildStats } from './../../utils/stats.js';

class Vanity extends Component {
	constructor(props) {
		super(props)
	}

	render(){
		const {
			manifest,
			weapon
		} = this.props;

		return (
			<div className="weapon__wrapper">
				{ 
					weapon.hash !== null 
						? buildStats(manifest, weapon.hash) : null
				}
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, { 

})(Vanity);
