import React, { Component } from 'react';
import { connect } from 'react-redux';
import { buildPerks } from '../../utils/perks.js';


class Perks extends Component {
	constructor(props) {
		super(props)

		const { manifest, weapon } = this.props;

		this.state = {
			perks: buildPerks(manifest, weapon)
		}
	}

	componentDidMount() {
		// const { manifest, weapon } = this.props;
		// if ( weapon.hash ) {
		// 	buildPerks(manifest, weapon);
		// }
	}

	componentDidUpdate(prevProps) {
		const { manifest, weapon } = this.props;
		if ( weapon.hash !== prevProps.weapon.hash ) {
			buildPerks(manifest, weapon);
		}
	}

	render(){
		const {
			state: {
				perks
			},
			props: {
				manifest,
				weapon
			}
		} = this;

		console.log(perks)

		return (
			<div className="weapon__perks">
				<span className="uppercase tracked-wide">Weapon Perks</span>
				<div className="perk-grid__wrapper">
					{ JSON.stringify(perks) }
				</div>
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, {})(Perks);
