import React, { Component } from 'react';
import { connect } from 'react-redux';
import { buildStats, buildEnhancedStat } from './../../utils/stats.js';

class Stats extends Component {
	constructor(props) {
		super(props)

		this.state = {
			displayInfo: {},
			baseStats: [],
			perks: [],
		}
	}

	componentDidMount() {
		const { manifest, weapon } = this.props;
		if ( weapon.hash ) {
			console.log(
				Object.values(weapon.perks).map(perk => {
					if (perk.hash !== 0) {
						return buildEnhancedStat(
							manifest,
							manifest.DestinyInventoryItemDefinition[weapon.hash],
							manifest.DestinyInventoryItemDefinition[1300023272]
						)
					}

				})
			)


			this.setState({
				displayInfo: manifest.DestinyInventoryItemDefinition[weapon.hash].displayProperties,
				baseStats: buildStats(manifest, weapon.hash),
				perks: weapon.perks
			});
		}
	}

	componentDidUpdate(prevProps) {
		const { manifest, weapon } = this.props;
		if ( weapon.hash !== prevProps.weapon.hash ) { 

			this.setState({
				displayInfo: manifest.DestinyInventoryItemDefinition[weapon.hash].displayProperties,
				baseStats: buildStats(manifest, weapon.hash),
				perks: weapon.perks
			});
		}
	}

	render(){
		const {
			baseStats
		} = this.state;
		const { manifest, weapon } = this.props;
		const weaponSubType = manifest.DestinyInventoryItemDefinition[weapon.hash].itemSubType;

		return (
			<div className="weapon__stats">

				<ul className="stat-list">
					{ 
						baseStats.map(stat => {
							if ( stat !== undefined ) { 
								const { 
									statHash,
									displayProperties,
									value
								} = stat;

								if ( weaponSubType === 18 && statHash === 3555269338) {
									return null;
								}

								return (
									<li key={statHash}>
										{ displayProperties.name }: { value } 
									</li>
								)
							}

							return true;
						})
					}
				</ul>
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, { 

})(Stats);
