import React, { Component } from 'react';
import { connect } from 'react-redux';
import { buildStats, enhanceStatsWithPlugs } from './../../utils/stats.js';

class Stats extends Component {
	constructor(props) {
		super(props)

		this.state = {
			displayInfo: {},
			baseStats: [],
		}
	}

	componentDidMount() {
		const { manifest, weapon } = this.props;
		if ( weapon.hash ) {
			const { stats, mwDiff } = buildStats(manifest, weapon.hash);
			this.setState({
				displayInfo: manifest.DestinyInventoryItemDefinition[weapon.hash].displayProperties,
				baseStats: stats,
				mwDiff: mwDiff,
			});
		}
	}

	componentDidUpdate(prevProps) {
		const { manifest, weapon } = this.props;

		if ( weapon.hash !== prevProps.weapon.hash ) {
			const { stats, mwDiff } = buildStats(manifest, weapon.hash);
			this.setState({
				displayInfo: manifest.DestinyInventoryItemDefinition[weapon.hash].displayProperties,
				baseStats: stats,
				mwDiff: mwDiff,
			});
		}
	}

	render(){
		const { manifest, weapon } = this.props;
		const { baseStats } = this.state;
		const weaponSubType = manifest.DestinyInventoryItemDefinition[weapon.hash].itemSubType;

		const enhancedStats = enhanceStatsWithPlugs(
			buildStats(manifest, weapon.hash),
			weapon.hash,
			weapon.perks, 
			weapon.mod, 
			weapon.masterwork
		).stats;

		return (
			<div className="weapon__stats">

				<ul className="stat-list">
					{ 
						enhancedStats.map(stat => {
							if ( stat !== undefined ) {
								const {
									statHash,
									displayProperties,
									bar,
									value
								} = stat;

								const baseStat = baseStats.filter(base => {
									return base ? (
										base.statHash === stat.statHash
									) : (
										null
									)
								});

								const baseVal = baseStat.length > 0 ? baseStat[0].value : 0;
								let modifier = baseStat.length > 0 ? stat.value - baseVal : 0;
								// let masterwork = baseStat.length > 0 ? stat.value - baseVal : 0;


								// Hide the Zoom stat for Swords.
								if (weaponSubType === 18 && statHash === 3555269338) return null

								// Bar stats
								return (
									<li key={statHash} className="stat__container">
										<div className="stat__name">
											<span>{ displayProperties.name }</span>
										</div>
										<div className={`stat__data ${bar ? 'bar' : 'num'}`}>
											{
												bar 
													? (
														<React.Fragment>
															<progress 
																className='main' 
																value={value} 
																max="100">
																{value}
															</progress> 
															<progress 
																className={`modifier ${modifier >= 0 ? modifier !== 0 ? 'positive' : 'neutral' : 'negative'}`} 
																value={modifier >= 0 ? modifier : -modifier}
																max="100"
																style={ modifier >= 0 
																		? {left: `${baseVal}%`} 
																		: {left: `-${100 - baseVal}%`}
																}
																>
																{modifier}
															</progress>
															{/* <progress 
																className="masterwork"
																value={masterwork > 0 ? masterwork : 0}
																max="100"
																style={ {left: `${baseVal}%`} }
																>
																{masterwork}
															</progress> */}

															<span className={`benefit-value ${modifier >= 0 ? modifier !== 0 ? 'positive' : 'neutral' : 'negative'}`}>
																{
																	modifier >= 0 
																		? modifier === 0
																			? '±' + modifier 
																			: '+' + modifier
																		: modifier
																}
															</span>
														</React.Fragment>
													) : (
														<span className={`stat__value ${
															modifier >= 0 
																? modifier !== 0 
																	? stat.smallerIsBetter 
																		? 'negative reverse'
																		: 'positive'
																	: 'neutral' 
																: stat.smallerIsBetter 
																	? 'positive reverse'
																	: 'negative'
														}`}>
															{value}
														</span>
													)
											}
										</div>
									</li>
								)
							}

							return true;
						})
					}
				</ul>
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, { 

})(Stats);
