import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchManifest } from './../actions/manifestActions';

import Header from './Header';

class Base extends Component {
	constructor(props) {
		super(props)

		this.state = {
			manifestLoaded: false
		}
	}
	componentDidMount() {
		this.props.fetchManifest();
		const { manifest } = this.props;


		if ( Object.keys((manifest)).length >= 1) {
			this.setState({
				manifestLoaded: true
			})
		}
	}

	componentDidUpdate() {
		console.log(this.props.manifest)
	}

	render() {
		const { manifestLoaded } = this.state;

		return (
			<React.Fragment>
				<Header manifestLoaded={ manifestLoaded }/>
				<main>

				</main>
			</React.Fragment>
		)
	}
}

Base.propTypes = {
	fetchManifest: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
	manifest: state.manifest
});

export default connect(mapStateToProps, { fetchManifest })(Base);
