import React, { Component } from 'react';
import { connect } from 'react-redux';
import { storePerks } from './../../actions/weaponActions';

class Weapon extends Component {
	constructor(props) {
		super(props)

		const {
			manifest,
			weapon
		} = this.props;

		this.state = {
			weapon: manifest.DestinyInventoryItemDefinition[this.props.weapon.hash]
		}
	}
	componentDidMount() {
		// const weapon = manifest.DestinyInventoryItemDefinition[weapon.hash];
	}

	render(){
		const {
			props: {
				manifest,
				weapon
			}
		} = this;
		console.log(this.state.weapon)
		return (
			<div className="weapon__wrapper">
				weapon
				{/* <Investment /> */}
				{ console.log() }

			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, { 
	storePerks
})(Weapon);
