import React, { Component } from 'react';
import { connect } from 'react-redux';
import { buildPerks } from '../../utils/perks.js';


class Perks extends Component {
	constructor(props) {
		super(props)

		this.state = {
			perks: []
		}
	}

	componentDidMount() {
		const { manifest, weapon } = this.props;
		if ( weapon.hash ) {
			this.setState({ perks: buildPerks(manifest, weapon) });
		}
	}

	componentDidUpdate(prevProps) {
		const { manifest, weapon } = this.props;
		if ( weapon.hash !== prevProps.weapon.hash ) {
			this.setState({ perks: buildPerks(manifest, weapon) });
		}
	}

	hasDuplicates = (array) => {
		// return (new Set(array)).size !== array.length;

		const dupes = array.reduce((acc, v, i, arr) => arr.indexOf(v) !== i && acc.indexOf(v) === -1 ? acc.concat(v) : acc, []);
	}

	render(){
		const {
			state: {
				perks
			},
			props: {
				manifest,
			}
		} = this;

		console.log(this.hasDuplicates(perks))

		return (
			<div className="weapon__perks">
				<span className="uppercase tracked-wide underline">Weapon Perks</span>
				<div className="perk-grid__wrapper">
					{
						perks.map((perkColumn, index) => {
							return (
								<ul className="perk-grid__column" key={index}>
									{
										perkColumn.map(perk => {
											const perkDef = manifest.DestinyInventoryItemDefinition[perk.hash];
											// if () {
											// 	return (
											// 		<li key={ `${perk.hash}-${perk.isCurated ? 'curated' : 'random'}` }>
											// 			{ perkDef.displayProperties.name }
											// 			{ perk.isCurated === true ? ' / curated!' : null}
											// 		</li>
											// 	)
											// } else {

											// }
										})
									}
								</ul>
							)
						})
					}
				</div>
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, {})(Perks);
