import React, { Component } from 'react';
import { connect } from 'react-redux';

class Vanity extends Component {


	render(){
		const {
			manifest,
			weapon
		} = this.props;

		const currentWeapon = manifest.DestinyInventoryItemDefinition[weapon.hash];

		return (
			<div className="weapon__vanity" style={{backgroundImage: `url(https://bungie.net${ currentWeapon.screenshot })`}}>
				<div className="icon" style={{backgroundImage: `url(https://bungie.net${ currentWeapon.displayProperties.icon })`}}></div>
				<div className="title">
					<h1 className="uppercase tracked-wide">{ currentWeapon.displayProperties.name }</h1>
					<span className="uppercase ">{ currentWeapon.itemTypeDisplayName }</span>
				</div>
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, { 

})(Vanity);
