import React, { Component } from 'react';
import { connect } from 'react-redux';
import { storePerks } from './../../actions/weaponActions';
import { buildStats } from './../../utils/stats.js';

class Weapon extends Component {
	constructor(props) {
		super(props)
	}

	render(){
		const {
			manifest,
			weapon
		} = this.props;

		return (
			<div className="weapon__wrapper">
				weapon
				{ 
					weapon.hash !== null ? buildStats(manifest, weapon) : null
				}
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, { 
	storePerks
})(Weapon);
