import React, { Component } from 'react';
import { connect } from 'react-redux';
import { buildPerks } from '../../utils/perks.js';


class Perks extends Component {
	constructor(props) {
		super(props)

		this.state = {
			perks: []
		}
	}

	componentDidMount() {
		const { manifest, weapon } = this.props;
		if ( weapon.hash ) {
			console.log(buildPerks(manifest, weapon))
			this.setState({ perks: buildPerks(manifest, weapon).perkList });
		}
	}

	componentDidUpdate(prevProps) {
		const { manifest, weapon } = this.props;
		if ( weapon.hash !== prevProps.weapon.hash ) {
			console.log(buildPerks(manifest, weapon))
			this.setState({ perks: buildPerks(manifest, weapon).perkList });
		}
	}

	render(){
		const {
			state: {
				perks
			},
			props: {
				manifest,
				weapon
			}
		} = this;

		return (
			<div className="weapon__perks">
				<span className="uppercase tracked-wide">Weapon Perks</span>
				<div className="perk-grid__wrapper">
					{
						perks.map((perkColumn, index) => {
							return (
								<ul className="perk-grid__column" key={index}>
									{
										perkColumn.map(perk => {
											const perkDef = manifest.DestinyInventoryItemDefinition[perk];
											return (
												<li key={perk}>
													{ perkDef.displayProperties.name }
												</li>
											)
										})
									}
								</ul>
							)
						})
					}
				</div>
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, {})(Perks);
