import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchManifest, storeManifestStatus } from './../actions/manifestActions';

import Header from './Header';

class Base extends Component {
	constructor(props) {
		super(props)

		this.state = {
			manifestLoaded: false
		}
	}
	componentDidMount() {
		this.props.fetchManifest();
	}

	componentDidUpdate(prevProps) {
		const { manifest } = this.props;
		console.log(this.props)
		if ( Object.keys((manifest)).length !== 0 && manifest !== prevProps.manifest) {
			// this.storeManifestState();

			this.setState({
				manifestLoaded: true
			})
		}
	}

	render() {
		const { manifestLoaded } = this.state;

		return (
			<React.Fragment>
				<Header />
				<main>

				</main>
			</React.Fragment>
		)
	}
}

Base.propTypes = {
	fetchManifest: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
	manifest: state.manifest
});

export default connect(mapStateToProps, { fetchManifest, storeManifestStatus })(Base);
