import React, { Component } from 'react';
// import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import SearchInput from './SearchInput';
import Filters from './Filters';
import Suggestions from './Suggestions';
import { storeWeapon, storePerks } from './../../actions/weaponActions';
import { storeRarity, storeWeaponType, storeDamage } from './../../actions/filterActions';

class Search extends Component {
	constructor(props) {
		super(props)

		this.state = {
			weaponList: [],
			filteredSuggestions: [],
			showSuggestions: false,
			userInput: '',
			manifestLoaded: false,
			filterView: 'hide',
			toggleFilter: () => {
				this.setState(({ filterView }) => ({
					filterView: filterView === 'hide' ? 'show' : 'hide'
				}))
			},
		}
	}

	componentDidUpdate(prevProps) {
		const { manifest } = this.props;

		if ( Object.keys((manifest)).length !== 0 && manifest !== prevProps.manifest) {
			let weaponList = [];

			// All Weapons
			Object.keys(manifest.DestinyInventoryItemDefinition).map(weapon => {
				return manifest.DestinyInventoryItemDefinition[weapon].itemType === 3
					? weaponList.push(manifest.DestinyInventoryItemDefinition[weapon])
					: null
			})

			// const filters = nextProps.filters;

			// Weapon Rarity
			// if ( filters.rarity ) {
			// 	weaponList = weaponList.filter(weapon => {
			// 		return weapon.inventory.tierTypeHash === filters.rarity 
			// 	})
			// }

			// // Weapon Type
			// if ( filters.weaponType ) {
			// 	weaponList = weaponList.filter(weapon => {
			// 		return weapon.itemSubType === filters.weaponType 
			// 	})
			// }

			// // Damage Type
			// if ( filters.damage ) {
			// 	weaponList = weaponList.filter(weapon => {
			// 		return weapon.defaultDamageTypeHash === filters.damage 
			// 	})
			// }

			this.setState({ 
				manifestLoaded: true,
				weaponList: weaponList,
				filteredSuggestions: weaponList
			})
		}
	}

	// SearchInput.js
	onInputChange = e => {
		const suggestions = this.state.weaponList;
		const userInput = e.currentTarget.value;
		console.log(suggestions, userInput)

		const filteredSuggestions = suggestions.filter(
			suggestion => (suggestion.displayProperties.name).toLowerCase().indexOf(userInput.toLowerCase()) > -1
		);

		this.setState({
			activeSuggestion: 0,
			filteredSuggestions,
			showSuggestions: true,
			userInput: e.currentTarget.value
		})

		console.log('searchInput', filteredSuggestions)
	}

	// Filters.js
	changeDamage = (e) => {
		let toStore = e.target.value === 0 ? null : parseInt(e.target.value);
		this.setState({ currentDamage: toStore, });
		this.props.storeDamage(toStore);
	}
	changeWeaponType = (e) => {
		let toStore = e.target.value === 0 ? null : parseInt(e.target.value);
		this.setState({ currentWeaponType: toStore,}); 
		this.props.storeWeaponType(toStore);
	}
	changeRarity = (e) => {
		let toStore = e.target.value === 0 ? null : parseInt(e.target.value);
		this.setState({ currentRarity: toStore, }); 
		this.props.storeRarity(toStore);
	}

	// onSuggestionClick = (hash) => {
	// 	this.props.storeWeapon(hash);	
	// 	this.props.storePerks(null, 0);
	// 	this.props.storePerks(null, 1);
	// 	this.props.storePerks(null, 2);
	// 	this.props.storePerks(null, 3);

	// 	this.setState({
	// 		activeSuggestion: 0,
	// 		filteredSuggestions: [],
	// 		showSuggestions: false,
	// 		userInput: ''
	// 	})

	// 	console.log()
	// }

	render() {
		const {
			onInputChange,
			changeRarity,
			changeWeaponType,
			changeDamage,
			state: {
				weaponList,
				filteredSuggestions,
				showSuggestions,
				userInput,
				manifestLoaded,
				filterView, 
				toggleFilter,
			},
			props: {
				manifest
			},
		} = this;

		return (
			<div className="search__wrapper">
				<SearchInput 
					onInputChange={ onInputChange }
					userInput={ userInput }
					manifestLoaded={ manifestLoaded }
				/>
				<Filters 
					changeRarity={ changeRarity }
					changeWeaponType={ changeWeaponType }
					changeDamage={ changeDamage }
				/>
				<Suggestions 

				/>
			</div>
		)
	}
}

// Autocomplete.propTypes = {
// 	storeWeapon: PropTypes.func.isRequired,
// 	storePerks: PropTypes.func.isRequired
// };

const mapStateToProps = state => ({
	manifest: state.manifest,
	filters: state.filters,
});

export default connect(mapStateToProps, { 
	storeWeapon, storePerks,
	storeRarity, storeWeaponType, storeDamage,
})(Search);
