import React, { Component } from 'react';
import { connect } from 'react-redux';
import { storeTooltipCoords } from './../../actions/tooltipActions';
import Current from './Current';
import Vanity from './Vanity';
import Perks from './Perks';
import Mods from './Mods';
import Masterworks from './Masterworks';

class Weapon extends Component {
	constructor(props) {
		super(props)

		this.state = {
			offsetX: 20,
			offsetY: 0
		}
	}

	tooltipHandler = () => {
		let x = window.event.x,
			y = window.event.y;

		let xChanged = false,
			yChanged = false;

		// console.log(x + 380, window.innerWidth)

		if ((x + 380) >= window.innerWidth) {
			console.log(-Math.abs((x+380) - window.innerWidth))
			// x = Math.abs((x+380) - window.innerWidth)
			// xChanged = true;
		}

		// if ((x + 380) > window.innerWidth) {
		// 	x = window.innerWidth - 360;
		// 	xChanged = true;
		// }

		if ( xChanged && yChanged ) {
			this.setState({
				offsetX: -x,
				offsetY: y
			})
		} else if ( xChanged ) {
			this.setState({
				offsetX: -x
			})
		} else if ( yChanged ) {
			this.setState({
				offsetY: y
			})
		} else {
			// do nothing
		}
	}

	render(){
		const {
			state: {
				offsetX,
				offsetY
			},
			props: {
				weapon 
			}
		} = this;

		if ( weapon.hash !== null ) {
			return (
				<div className="weapon__wrapper">
					<Vanity />
					<Perks />
					<Mods 
						tooltipHandler={() => this.tooltipHandler()}
						offsetX={ offsetX }
						offsetY={ offsetY }
					/>
					<Masterworks 
						tooltipHandler={() => this.tooltipHandler()}
						offsetX={ offsetX }
						offsetY={ offsetY }
					/>
					<Current />
				</div>
			)
		} else {
			return (
				<div className="weapon__wrapper empty">
					No gun selected.
				</div>
			)
		}
	}
}

const mapStateToProps = state => ({
	weapon: state.weapon,
});

export default connect(mapStateToProps, {})(Weapon);
