import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getPerks } from './../../utils/perks.js';
import { buildPerks } from '../../utils/perks.js';


class Perks extends Component {
	constructor(props) {
		super(props)
	}

	omponentDidMount() {
		const { manifest, weapon } = this.props;
		if ( weapon.hash ) {
			buildPerks(manifest, weapon)
			// this.setState({
			// 	displayInfo: manifest.DestinyInventoryItemDefinition[weapon.hash].displayProperties,
			// 	baseStats: buildStats(manifest, weapon.hash),
			// });
		}
	}

	componentDidUpdate(prevProps) {
		const { manifest, weapon } = this.props;
		if ( weapon.hash !== prevProps.weapon.hash ) { 

			buildPerks(manifest, weapon);
			// this.setState({
			// 	displayInfo: manifest.DestinyInventoryItemDefinition[weapon.hash].displayProperties,
			// 	baseStats: buildStats(manifest, weapon.hash),
			// });
		}
	}

	render(){
		return (
			<div className="weapon__perks">
				<span className="uppercase tracked-wide">Weapon Perks</span>
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, {})(Perks);
