let manifest;

export function buildPerks(
	theManifest,
	weapon
) {
	console.log('building perks');
	manifest = theManifest;

	const sockets = weapon.sockets;
	const socketEntries = weapon.sockets.socketEntries;

	socketEntries.map(socket => {
		if (socket.plugSources === 2) {
			console.log(socket.reusablePlugItems);
		}
	});
}
