import React, { Component } from 'react';
import { connect } from 'react-redux';
import { buildStats } from './../../../utils/stats.js';

class Vanity extends Component {
	constructor(props) {
		super(props)

		this.state = {
			baseStats: [],
			displayInfo: {},
		}
	}

	componentDidMount() {
		const { manifest, weapon } = this.props;
		if ( weapon.hash ) {
			this.setState({
				displayInfo: manifest.DestinyInventoryItemDefinition[weapon.hash].displayProperties,
				baseStats: buildStats(manifest, weapon.hash),
			});
		}
	}

	componentDidUpdate(prevProps) {
		const { manifest, weapon } = this.props;
		if ( weapon.hash !== prevProps.weapon.hash ) { 
			this.setState({
				displayInfo: manifest.DestinyInventoryItemDefinition[weapon.hash].displayProperties,
				baseStats: buildStats(manifest, weapon.hash),
			});
		}
	}

	render(){
		const {
			baseStats
		} = this.state;
		const { manifest, weapon } = this.props;
		const weaponSubType = manifest.DestinyInventoryItemDefinition[weapon.hash].itemSubType;
		console.log(this.state.baseStats)

		return (
			<div className="weapon__wrapper">
				<ul className="stat-list">
					{ 
						baseStats.map(stat => {
							console.log(stat)

							if ( stat !== undefined ) { 
								const { 
									statHash,
									displayProperties,
									value
								} = stat;

								if ( weaponSubType === 18) {
									return null;
								}

								return (
									<li key={statHash}>
										{ displayProperties.name }: { value }
									</li>
								)
							}
						})
					}
				</ul>
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, { 

})(Vanity);
