import React, { Component } from 'react';
import { connect } from 'react-redux';
import { buildStats, enhanceStatsWithPlugs } from './../../utils/stats.js';

class Stats extends Component {
	constructor(props) {
		super(props)

		this.state = {
			displayInfo: {},
			baseStats: [],
			perks: [],
			stats: []
		}
	}

	componentDidMount() {
		const { manifest, weapon } = this.props;
		const perks = weapon.perks;
		if ( weapon.hash ) {
			this.setState({
				displayInfo: manifest.DestinyInventoryItemDefinition[weapon.hash].displayProperties,
				baseStats: buildStats(manifest, weapon.hash),
				stats: enhanceStatsWithPlugs(
					buildStats(manifest, weapon.hash),
					weapon.hash,
					weapon.perks, 
					weapon.mod, 
					weapon.masterwork
				)
			});
		}
	}

	// componentwillre

	componentDidUpdate(prevProps, prevState) {
		const { manifest, weapon } = this.props;
		const oldP = prevProps.weapon.perks;
		const newP = weapon.perks;
		const altP = this.state.perks;

		console.log(
			prevProps.weapon.perks,
			this.props.weapon.perks,
			this.state.perks
			// 'old', oldP, '\n',
			// 'new', newP, '\n',
			// 'alt', altP, '\n',
		)

		// Object.keys(oldP).map(perkSlot => {
		// 	console.log(oldP[perkSlot].hash, newP[perkSlot].hash)
		// 	if (oldP[perkSlot].hash === newP[perkSlot].hash) {
		// 		console.log(true)
		// 	}
		// })

		if ( weapon.hash !== prevProps.weapon.hash ) { 	
			this.setState({
				displayInfo: manifest.DestinyInventoryItemDefinition[weapon.hash].displayProperties,
				baseStats: buildStats(manifest, weapon.hash),
				stats: enhanceStatsWithPlugs(
						buildStats(manifest, weapon.hash),
						weapon.hash,
						weapon.perks, 
						weapon.mod, 
						weapon.masterwork
					)
			});
		}



		// if () {

			// enhanceStatsWithPlugs(
			// 	buildStats(manifest, weapon.hash),
			// 	weapon.hash,
			// 	weapon.perks, 
			// 	weapon.mod, 
			// 	weapon.masterwork
			// )
		// }
	}

	render(){
		const {
			stats
		} = this.state;
		const { manifest, weapon } = this.props;
		const weaponSubType = manifest.DestinyInventoryItemDefinition[weapon.hash].itemSubType;

		const enhancedStats = enhanceStatsWithPlugs(
			buildStats(manifest, weapon.hash),
			weapon.hash,
			weapon.perks, 
			weapon.mod, 
			weapon.masterwork
		);

		console.log(enhancedStats)

		return (
			<div className="weapon__stats">

				<ul className="stat-list">
					{ 
						stats.map(stat => {
							if ( stat !== undefined ) { 
								const { 
									statHash,
									displayProperties,
									value
								} = stat;

								if ( weaponSubType === 18 && statHash === 3555269338) {
									return null;
								}

								return (
									<li key={statHash}>
										{ displayProperties.name }: { value } 
									</li>
								)
							}

							return true;
						})
					}
				</ul>
			</div>
		)
	}
}

const mapStateToProps = state => ({
	manifest: state.manifest,
	weapon: state.weapon,
});

export default connect(mapStateToProps, { 

})(Stats);
